css:
.info .text-left {
    color: black;
    padding-right: 0;
}
.info .no-padding {
    padding-left: 0;
    padding-right: 0;
}
    
.text-list{
    padding-top: 0.1rem;
}
.detail{
    color: black;
    font-size: 24px;
}

.hrline {
  margin-top: 20px;
  border: 0;
  border-bottom: 1px solid rgba(0, 0, 0, 0.1);
}

.section1 {
    border: 1px solid black;
    padding: 10px;  /* Adjust the padding for space inside the border */
    margin-bottom: 5px;
}

.section2 {
    border: 1px solid black;
    padding: 10px;  /* Adjust the padding for space inside the border */
}
.tax-id-container {
    display: flex;
    align-items: center;
    gap: 4px;
    margin-bottom: 5px ;
}

.tax-id-label {
    margin-right: 8px;
}


.tax-id-group {
    display: flex;
    gap: 0; /* Remove gap between connected boxes */
}

.tax-id-box {
    width: 30px;
    height: 30px;
    border: 1px solid black;
    display: flex;
    align-items: center;
    justify-content: center;
    font-size: 16px;
}

.zip-code-group {
    display: flex;
    gap: 0;
}

.zip-code-box {
    width: 15px;
    height: 15px;
    border: 1px solid black;
    display: flex;
    align-items: center;
    justify-content: center;
    font-size: 14px;
}

.amount-box1 {
    border: 1px solid black;
    border-right: none; /* Removes the right border */
    font-size: 13px;
    width: auto; /* Set to auto if you don't want a fixed width */
    height: 13px; /* Set a fixed height */
    min-width: auto; /* Optional: Ensures minimum width */
    min-height: 15px; /* Optional: Ensures minimum height */
    display: flex;
    align-items: center; /* Center content vertically */
    justify-content: flex-end; /* Align content to the right */
    padding-right: 5px; /* Optional: Adds padding on the right for spacing */
}

.amount-box2 {
    border: 1px solid black;
    /*padding-right: 5px;*/
    font-size: 13px;
    width: 55px; /* Set a fixed width */
    height: 15px; /* Set a fixed height */
    min-width: 55px; /* Optional: Ensures minimum width */
    min-height: 15px; /* Optional: Ensures minimum height */
    display: flex; /* Center content if needed */
    align-items: center; /* Center content vertically */
    justify-content: flex-end; /* Align content to the right */
}

.expanded-message{
    color: #585858;
    font-size: 10px;
}
.amount-value {
    border-collapse: collapse;
    width: 100%; /* Optional: makes the table take up full width */
}

.amount-value {
    border-collapse: collapse; /* Ensures borders don't have gaps */
}

.amount-value th.am1 {
    border: 1px solid black;
    height: 10px;
    padding: 0;
    text-align: right;
    width: 95px; /* Sets a minimum width for this cell */
}

.amount-value th.am2 {
    border: 1px solid black;
    height: 10px;
    padding: 0;
    text-align: right;
    width: 25px; /* Sets a minimum width for this cell */
}

.amount-value th.am1-6 {
    border: 1px solid black;
    height: 10px;
    padding: 0;
    text-align: right;
    width: 150px; /* Sets a minimum width for this cell */
}

.amount-value th.am2-6 {
    border: 1px solid black;
    height: 10px;
    padding: 0;
    text-align: right;
    width: 37px; /* Sets a minimum width for this cell */
}

@media screen {
	.print-format-gutter {
		background-color: #d1d8dd;
		padding: 30px 0px;
	}
	.print-format {
		background-color: white;
		border-radius: 8px;
		max-width: 8.3in;
		min-height: 11.69in;
		padding: 0.25in;
		margin: auto;
		color: var(--gray-900);
	}

	.print-format.landscape {
		max-width: 11.69in;
		padding: 0.2in;
	}

	.page-break {
		/* padding: 15px 0px; */
		border-bottom: 1px dashed #888;
	}

	/* .page-break:first-child {
		padding-top: 0px;
	} */

	.page-break:last-child {
		border-bottom: 0px;
	}

	/* mozilla hack for images in table */
	body:last-child .print-format td img {
		width: 100% !important;
	}

	@media(max-width: 767px) {
		.print-format {
			padding: 0.2in;
		}
	}
}

@media print {
	.print-format p {
		margin-left: 1px;
		margin-right: 1px;
	}
}

.disabled-check {
	color: #eee;
}

.data-field {
	margin-top: 5px;
	margin-bottom: 5px;
}

.data-field .value {
	word-wrap: break-word;
}

.important .value {
	font-size: 8px;
	font-weight: bold;
}

.important label {
	line-height: 2;
	margin: 0px;
}

.table {
	font-size: inherit;
	margin: 3px 0px;
	border-collapse: collapse;
}

.checkbox-options {
	columns: var(--checkbox-options-columns);
}

.print-item-image {
	object-fit: contain;
}

.pdf-variables,
.pdf-variable,
.visible-pdf {
	display: none !important;
}

.print-format {
	font-size: 9pt;
	font-family: Inter, "Helvetica Neue", Helvetica, Arial, "Open Sans", sans-serif;
	-webkit-print-color-adjust:exact;
}

.page-break {
	page-break-after: always;
}

.print-heading {
	border-bottom: 1px solid #aaa;
	margin-bottom: 10px;
}

.print-heading h2 {
	margin: 0px;
}
.print-heading h4 {
	margin-top: 5px;
}

table.no-border, table.no-border td {
	border: 0px;
}

.print-format label {
	/* wkhtmltopdf breaks label into multiple lines when it is inline-block */
	display: block;
	font-weight: 700;
}

.print-format img {
	max-width: 100%;
}

.print-format table td > .primary:first-child {
	font-weight: bold;
}

.print-format td, .print-format th {
	vertical-align: top !important;
	padding: 5px !important;
}

.print-format p {
	margin: 3px 0px 3px;
}

.print-format table td pre {
	white-space: normal;
	word-break: normal;
}

table td div {
	
	/* needed to avoid partial cutting of text between page break in wkhtmltopdf */
	page-break-inside: avoid !important;
	
}

/* hack for webkit specific browser */
@media (-webkit-min-device-pixel-ratio:0) {
	thead, tfoot {
		display: table-header-group;
	}
}

[document-status] {
	margin-bottom: 5mm;
}


.print-format-preview [data-fieldtype="Table"] {
	overflow: auto;
}
.print-format {
    font-size: 13px;
    background: white;
}

.print-heading {
    border-bottom: 1px solid #f4f5f6;
    padding-bottom: 5px;
    margin-bottom: 10px;
}

.print-heading h2 {
    font-size: 24px;
}

.print-heading h2 div {
    font-weight: 600;
}

.print-heading small {
    font-size: 13px !important;
    font-weight: normal;
    line-height: 2.5;
    color: #4c5a67;
}

.print-format .letter-head {
    margin-bottom: 30px;
}

.print-format label {
    font-weight: normal;
    font-size: 14px;
    color: #000;
    margin-bottom: 0;
}

.print-format .data-field {
    margin-top: 0;
    margin-bottom: 0;
}

.print-format .value {
    color: #192734;
    line-height: 1;
}

.print-format .section-break:not(:last-child) {
    margin-bottom: 0;
}

.print-format .row:not(.section-break) {
    line-height: 1.2;
    margin-top: 3px !important;
    margin-bottom: 3px !important;
}

.print-format .important .value {
    font-size: 13px;
    font-weight: 600;
}

.print-format th {
    color: black;
    font-weight: normal;
    border-bottom-width: 1px !important;
    background-color: #FFF;
    font-size: 12px;
}

.print-format .table-bordered td, .print-format .table-bordered th {
    border: 1px solid black;
    border-collapse: collapse;
}

.print-format .table-bordered {
    border: 1px solid black;
}

.print-format td, .print-format th {
    padding: 2px !important;
}

.print-format .primary.compact-item {
    font-weight: normal;
}

.print-format table td .value {
    font-size: 12px;
    line-height: 1.8;
}
